    font-size: 0.9rem;
}

.class-details h4 {
    color: var(--light-text);
    font-size: 1.1rem;
    margin-bottom: 5px;
}

.class-details p {
    color: var(--gray-text);
    font-size: 0.9rem;
}

.class-status {
