.student-card .card-header {
    justify-content: space-between;
}

.search-box {
    position: relative;
    display: flex;
    align-items: center;
}

.search-box input {
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 25px;
    padding: 10px 20px 10px 45px;
    color: var(--light-text);
    font-size: 0.9rem;
    width: 250px;
    backdrop-filter: blur(10px);
}

.save-btn {
    position: absolute;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
    background: linear-gradient(135deg, var(--primary-color), var(--accent-color));
    border: none;
    border-radius: 50%;
    width: 30px;
    height: 30px;
    color: white;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
}

.save-btn:hover {
    transform: translateY(-50%) scale(1.1);
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.3);
}

.search-box i {
    position: absolute;
    left: 15px;
    color: var(--gray-text);
}

.student-avatar {
    width: 40px;
    height: 40px;
    background: linear-gradient(135deg, var(--primary-color), var(--accent-color));
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1rem;
}

.student-info {
    flex: 1;
}

.student-info .roll {
    display: block;
    color: var(--primary-color);
    font-weight: 600;
    font-size: 0.9rem;
    margin-bottom: 5px;
}

.student-info .name {
    color: var(--light-text);
    font-weight: 500;
}

.status-toggle {
    padding: 10px 20px;
    border-radius: 25px;
    font-size: 0.9rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 8px;
    border: none;
    min-width: 100px;
    justify-content: center;
}

.status-toggle.present {
    background: rgba(34, 197, 94, 0.2);
    color: #22c55e;
    border: 1px solid rgba(34, 197, 94, 0.3);
}

.status-toggle.absent {
    background: rgba(239, 68, 68, 0.2);
    color: #ef4444;
    border: 1px solid rgba(239, 68, 68, 0.3);
}

.status-toggle:hover {
    transform: scale(1.05);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
}

.qr-section {
    margin-bottom: 40px;
}

.qr-container {
    text-align: center;
}

.live-feed .feed-tools {
    display: flex;
    gap: 15px;
    flex-wrap: wrap;
    align-items: center;
    margin-bottom: 15px;
}

.live-feed .note-wrap {
    display: flex;
    gap: 10px;
    flex: 1;
}

.feed-list {
    display: flex;
    flex-direction: column-reverse;
    gap: 10px;
    max-height: 250px;
    overflow: auto;
}

.feed-item {
    background: rgba(255,255,255,0.06);
    border: 1px solid rgba(255,255,255,0.12);
    padding: 10px 12px;
    border-radius: 12px;
    display: flex;
    justify-content: space-between;
    color: var(--light-text);
}
.feed-item .feed-meta{ color: var(--gray-text); font-size: 0.85rem; }
.feed-item.flag{ border-color: rgba(239,68,68,0.5); box-shadow: 0 0 0 1px rgba(239,68,68,0.3) inset; }

.alert-bar{
    margin-top: 10px;
    background: rgba(239,68,68,0.2);
    color: #ef4444;
    padding: 10px 12px;
    border: 1px solid rgba(239,68,68,0.3);
    border-radius: 12px;
    font-weight: 600;
}

.controls-grid{ display:grid; grid-template-columns: 1fr 300px; gap:20px; }
.override-form{ display:flex; gap:10px; align-items:center; }
.analytics-grid{ display:grid; grid-template-columns: repeat(4, minmax(160px,1fr)); gap:15px; margin-bottom:15px; }
.mini-analytic{ background: rgba(255,255,255,0.06); border:1px solid rgba(255,255,255,0.12); border-radius:12px; padding:12px; }
.mini-analytic .label{ color: var(--gray-text); font-size: .85rem; }
.mini-analytic .value{ color: var(--light-text); font-weight:800; font-size:1.4rem; }
.analytic-lists{ display:grid; grid-template-columns: repeat(3, 1fr); gap:20px; }
.analytic-lists .list{ list-style:none; padding:0; margin:0; display:flex; flex-direction:column; gap:8px; }
.analytic-lists li{ background: rgba(255,255,255,0.06); border:1px solid rgba(255,255,255,0.12); padding:8px 10px; border-radius:10px; }

.qr-controls {
    display: flex;
    flex-direction: column;
    gap: 20px;
    margin-bottom: 30px;
    align-items: center;
}

.select-wrapper {
    position: relative;
    width: 100%;
    max-width: 400px;
}

.glass-select {
    width: 100%;
    padding: 15px 20px;
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 15px;
    color: var(--light-text);
    font-size: 1rem;
    backdrop-filter: blur(10px);
    cursor: pointer;
}

.button-group {
    display: flex;
    gap: 15px;
}

.glass-display {
    background: rgba(255, 255, 255, 0.03);
    border-radius: 20px;
    padding: 40px;
    margin-bottom: 30px;
    border: 2px dashed rgba(255, 255, 255, 0.2);
    backdrop-filter: blur(10px);
}

.qr-placeholder {
    text-align: center;
}

.qr-icon {
    font-size: 4rem;
    color: var(--gray-text);
    margin-bottom: 20px;
}

.qr-placeholder p {
    color: var(--gray-text);
    font-size: 1.1rem;
}

.qr-info {
    display: flex;
    justify-content: center;
    gap: 40px;
    flex-wrap: wrap;
}

.info-item {
    display: flex;
    align-items: center;
    gap: 10px;
    color: var(--gray-text);
}

.info-item i {
    color: var(--primary-color);
    font-size: 1.2rem;
}

.timer {
    color: var(--accent-color);
    font-weight: 600;
}

.attendance-section {
    margin-bottom: 40px;
}

.section-header {
    text-align: center;
    margin-bottom: 30px;
}

.gradient-title {
    background: linear-gradient(135deg, var(--primary-color), var(--accent-color));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    font-size: 2.5rem;
    font-weight: 800;
    margin-bottom: 15px;
}

.attendance-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 25px;
}

.stat-card {
    position: relative;
    overflow: hidden;
}

.stat-icon {
    width: 60px;
    height: 60px;
    background: linear-gradient(135deg, var(--primary-color), var(--accent-color));
    border-radius: 15px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.8rem;
    margin-bottom: 20px;
    box-shadow: 0 10px 20px rgba(59, 130, 246, 0.3);
}

.stat-content h3 {
    color: var(--gray-text);
    font-size: 1rem;
    margin-bottom: 10px;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.stat-content .stat-number {
    font-size: 2.5rem;
    font-weight: 800;
    color: var(--light-text);
}

.stat-progress {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 4px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
    overflow: hidden;
}

.progress-bar {
    height: 100%;
    background: linear-gradient(90deg, var(--primary-color), var(--accent-color));
    border-radius: 2px;
    transition: width 0.5s ease;
}

.progress-bar.present {
    background: linear-gradient(90deg, #22c55e, #16a34a);
}

.progress-bar.absent {
    background: linear-gradient(90deg, #ef4444, #dc2626);
}

.progress-bar.rate {
    background: linear-gradient(90deg, var(--accent-color), #f97316);
}

@media (max-width: 768px) {
    .faculty-header {
        flex-direction: column;
        gap: 20px;
        text-align: center;
    }

    .header-stats {
        justify-content: center;
    }

    .dashboard-grid {
        grid-template-columns: 1fr;
    }

    .qr-controls {
        align-items: stretch;
    }

    .button-group {
        flex-direction: column;
    }

    .attendance-stats {
        grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    }
}

/* Save Indicator Animation */
@keyframes slideIn {
    from {
        opacity: 0;
        transform: translateY(-10px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.save-indicator {
    z-index: 1000;
}

/* Testimonials Section */
.testimonials {
    padding: 100px 0;
    background: linear-gradient(135deg, rgba(30, 41, 59, 0.8) 0%, rgba(15, 23, 42, 0.8) 100%);
}

.testimonials-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 40px;
    margin-bottom: 80px;
}

.testimonial-card.testimonial-featured {
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid var(--glass-border);
    border-radius: 24px;
    padding: 40px;
    backdrop-filter: blur(20px);
    position: relative;
    overflow: hidden;
}

.testimonial-card.testimonial-featured::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 3px;
    background: var(--gradient-primary);
}

.testimonial-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 24px;
}

.testimonial-avatar {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    overflow: hidden;
    border: 3px solid var(--primary-color);
}

.testimonial-avatar img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.testimonial-info {
    flex: 1;
    margin-left: 20px;
}

.testimonial-name {
    font-size: 1.2rem;
    font-weight: 600;
    color: var(--light-text);
    margin-bottom: 4px;
}

.testimonial-title {
    font-size: 0.9rem;
    color: var(--gray-text);
    margin-bottom: 8px;
}

.testimonial-rating {
    display: flex;
    gap: 4px;
}

.testimonial-rating i {
    color: #fbbf24;
    font-size: 0.8rem;
}

.testimonial-logo {
    width: 50px;
    height: 50px;
    background: var(--gradient-secondary);
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.2rem;
}

.testimonial-content {
    margin-bottom: 32px;
}

.testimonial-content p {
    font-size: 1.1rem;
    color: var(--gray-text);
    line-height: 1.6;
    font-style: italic;
}

.testimonial-metrics {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 20px;
}

.testimonials-side {
    display: flex;
    flex-direction: column;
    gap: 24px;
}

.testimonial-card.testimonial-compact {
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid var(--border-color);
    border-radius: 20px;
    padding: 24px;
    transition: all 0.3s ease;
}

.testimonial-card.testimonial-compact:hover {
    transform: translateY(-4px);
    background: rgba(255, 255, 255, 0.05);
    border-color: var(--glass-border);
}

.testimonial-card.testimonial-compact .testimonial-header {
    margin-bottom: 16px;
}

.testimonial-card.testimonial-compact .testimonial-avatar {
    width: 50px;
    height: 50px;
    border-width: 2px;
}

.testimonial-card.testimonial-compact .testimonial-info {
    margin-left: 16px;
}

.testimonial-card.testimonial-compact .testimonial-name {
    font-size: 1.1rem;
}

.testimonial-card.testimonial-compact .testimonial-title {
    font-size: 0.85rem;
}

.testimonial-card.testimonial-compact .testimonial-content p {
    font-size: 1rem;
}

.testimonials-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 32px;
}

.stat-card {
    display: flex;
    align-items: center;
    gap: 20px;
    padding: 32px;
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid var(--border-color);
    border-radius: 20px;
    transition: all 0.3s ease;
}

.stat-card:hover {
    transform: translateY(-4px);
    background: rgba(255, 255, 255, 0.05);
    border-color: var(--glass-border);
}

.stat-icon {
    width: 60px;
    height: 60px;
    background: var(--gradient-primary);
    border-radius: 16px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.5rem;
}

.stat-content {
    flex: 1;
}

.stat-number {
    font-size: 2rem;
    font-weight: 700;
    color: var(--primary-color);
    display: block;
    margin-bottom: 4px;
}

.stat-label {
    font-size: 0.9rem;
    color: var(--gray-text);
    font-weight: 500;
}

/* Button Styles */
.btn {
    display: inline-flex;
    align-items: center;
    gap: 10px;
    padding: 15px 30px;
    border-radius: 50px;
    text-decoration: none;
    font-weight: 600;
    transition: all 0.3s ease;
    border: none;
    cursor: pointer;
    font-size: 1rem;
}

.btn-primary {
    background: var(--gradient-primary);
    color: white;
    box-shadow: var(--shadow-medium);
}

.btn-primary:hover {
    transform: translateY(-3px);
    box-shadow: var(--shadow-heavy);
}

.btn-secondary {
    background: transparent;
    color: var(--light-text);
    border: 2px solid var(--border-color);
}

.btn-secondary:hover {
    background: var(--glass-bg);
    border-color: var(--glass-border);
}

.btn-large {
    padding: 18px 40px;
    font-size: 1.1rem;
}

/* Responsive Design */
@media (max-width: 768px) {
    .hero-title {
        font-size: 2.5rem;
    }

    .hero-stats {
        flex-direction: column;
        gap: 20px;
    }

    .hero-buttons {
        flex-direction: column;
        align-items: center;
    }

    .testimonials-grid {
        grid-template-columns: 1fr;
    }

    .about-content {
        grid-template-columns: 1fr;
        gap: 40px;
    }

    .features-showcase {
        gap: 40px;
    }

    .feature-metrics {
        grid-template-columns: 1fr;
    }

    .cta-actions {
        flex-direction: column;
        align-items: center;
    }

    .cta-benefits {
        flex-direction: column;
        align-items: center;
    }

    .hero-showcase {
        max-width: 100%;
    }

    .floating-shapes {
        display: none;
    }

    .visual-container {
        height: 300px;
    }

    .visual-card {
        width: 250px;
        padding: 20px;
    }
}

@media (max-width: 480px) {
    .hero-title {
        font-size: 2rem;
    }

    .hero-showcase {
        margin-top: 30px;
    }

    .showcase-grid {
        grid-template-columns: 1fr;
    }

    .showcase-card h3 {
        font-size: 1.5rem;
    }

    .card-icon {
        width: 60px;
        height: 60px;
        font-size: 1.5rem;
    }

    .feature-card {
        padding: 30px 20px;
    }
}

/* Mobile Menu Styles */
@media (max-width: 768px) {
    .nav-toggle {
        display: flex;
    }

    .nav-menu {
        position: fixed;
        top: 70px;
        left: -100%;
        width: 100%;
        height: calc(100vh - 70px);
        background: rgba(15, 23, 42, 0.98);
        backdrop-filter: blur(20px);
        flex-direction: column;
        justify-content: flex-start;
        align-items: center;
        padding-top: 40px;
        gap: 30px;
        transition: left 0.3s ease;
        border-top: 1px solid var(--border-color);
    }

    .nav-menu.active {
        left: 0;
    }

    .nav-link {
        font-size: 1.2rem;
        padding: 15px 0;
    }

    .btn-login {
        margin-top: 20px;
        padding: 12px 30px;
        border: 2px solid var(--primary-color);
        border-radius: 25px;
    }

    /* Hero Section Mobile */
    .hero {
        padding: 100px 0 60px;
        text-align: center;
    }

    .hero-content {
        flex-direction: column;
    }

    .hero-text {
        margin-bottom: 40px;
    }

    .hero-title {
        font-size: 2.5rem;
    }

    .hero-description {
        font-size: 1.1rem;
    }

    .hero-stats {
        justify-content: center;
    }

    .hero-showcase {
        order: 2;
        margin-top: 40px;
    }

    /* Features Section Mobile */
    .features-showcase {
        gap: 40px;
    }

    .feature-card.feature-primary {
        padding: 30px 20px;
    }

    .feature-metrics {
        grid-template-columns: 1fr;
        gap: 16px;
    }

    .feature-actions {
        flex-direction: column;
        gap: 16px;
    }

    /* Testimonials Mobile */
    .testimonials-grid {
        grid-template-columns: 1fr;
        gap: 24px;
    }

    .testimonial-card.testimonial-featured {
        padding: 30px 20px;
    }

    .testimonial-metrics {
        grid-template-columns: 1fr;
    }

    .testimonials-stats {
        grid-template-columns: repeat(2, 1fr);
        gap: 20px;
    }

    .stat-card {
        padding: 20px;
    }

    /* About Section Mobile */
    .about-content {
        grid-template-columns: 1fr;
        gap: 40px;
        text-align: center;
    }

    .about-features {
        gap: 16px;
    }

    .about-feature {
        text-align: left;
    }

    .about-cta {
        justify-content: center;
        flex-wrap: wrap;
    }

    .visual-container {
        height: 300px;
    }

    .visual-card {
        width: 250px;
        padding: 20px;
    }

    /* CTA Section Mobile */
    .cta {
        padding: 80px 0;
    }

    .cta-title {
        font-size: 2.5rem;
    }

    .cta-description {
        font-size: 1.1rem;
    }

    .cta-benefits {
        flex-direction: column;
        align-items: center;
        gap: 20px;
    }

    .cta-actions {
        flex-direction: column;
        align-items: center;
        gap: 16px;
    }

    .cta-cards {
        grid-template-columns: 1fr;
        gap: 20px;
    }

    /* General Mobile Adjustments */
    .container {
        padding: 0 15px;
    }

    .section-title {
        font-size: 2.5rem;
    }

    .section-description {
        font-size: 1.1rem;
    }

    .floating-shapes {
        display: none;
    }

    .floating-icon {
        display: none;
    }
}

/* Tablet Styles */
@media (min-width: 769px) and (max-width: 1024px) {
    .hero-title {
        font-size: 3rem;
    }

    .testimonials-grid {
        grid-template-columns: 1fr;
        gap: 30px;
    }

    .about-content {
        gap: 60px;
    }

    .features-showcase {
        gap: 50px;
    }

    .cta-cards {
        grid-template-columns: repeat(2, 1fr);
    }
}

/* Large Screen Optimizations */
@media (min-width: 1025px) {
    .hero-content {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 80px;
        align-items: center;
    }

    .hero-showcase {
        max-width: 500px;
    }

    .testimonials-grid {
        grid-template-columns: 1fr 1fr;
        gap: 50px;
    }

    .about-content {
        gap: 100px;
    }

    .features-showcase {
        gap: 80px;
    }
}


/* Production QR System Styles */
.production-qr-container {
