    justify-content: space-between;
    align-items: center;
    margin-bottom: 1.5rem;
}

.qr-header h3 {
    color: var(--primary-color);
    font-size: 1.5rem;
    font-weight: 700;
}

.qr-timer {
    background: linear-gradient(135deg, #10b981, #059669);
    color: white;
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-weight: 700;
    font-size: 1.1rem;
    min-width: 80px;
}

.qr-info {
    background: rgba(0, 0, 0, 0.2);
    border-radius: 12px;
    padding: 1rem;
    margin-bottom: 1.5rem;
}

.info-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0.5rem 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.info-item:last-child {
