}

.info-item .label {
    color: var(--gray-text);
    font-weight: 600;
}

.info-item .value {
    color: var(--light-text);
    font-weight: 500;
}

.qr-code-display {
    background: white;
    padding: 1rem;
    border-radius: 12px;
    margin: 1.5rem 0;
    display: inline-block;
}

.qr-image {
    width: 250px;
    height: 250px;
    display: block;
}

.qr-stats {
    display: flex;
    justify-content: space-around;
    margin: 1.5rem 0;
}

.stat-item {
    text-align: center;
}

.stat-label {
    color: var(--gray-text);
    font-size: 0.9rem;
    display: block;
}

.stat-value {
    color: var(--primary-color);
    font-size: 1.5rem;
    font-weight: 700;
}

.btn-regenerate {
    background: linear-gradient(135deg, #10b981, #059669);
    color: white;
    border: none;
    padding: 0.75rem 1.5rem;
    border-radius: 8px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
}

.btn-regenerate:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-medium);
}

.qr-loading {
    padding: 3rem 2rem;
    text-align: center;
}

.loading-spinner {
    width: 40px;
    height: 40px;
    border: 4px solid rgba(59, 130, 246, 0.3);
    border-top: 4px solid var(--primary-color);
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin: 0 auto 1rem;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.qr-notification {
    position: fixed;
    top: 100px;
    right: 20px;
    padding: 1rem 1.5rem;
    border-radius: 8px;
    color: white;
    font-weight: 600;
    z-index: 10000;
    animation: slideInRight 0.3s ease-out;
}

.qr-notification.success {
    background: linear-gradient(135deg, #10b981, #059669);
}

.qr-notification.error {
    background: linear-gradient(135deg, #ef4444, #dc2626);
}

.qr-notification i {
    margin-right: 0.5rem;
}

@keyframes slideInRight {
    from {
        opacity: 0;
        transform: translateX(100px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

/* Mobile QR Optimizations */
@media (max-width: 768px) {
    .production-qr-container {
        padding: 1.5rem;
        margin: 1rem;
    }

    .qr-image {
        width: 200px;
        height: 200px;
    }

    .qr-header {
        flex-direction: column;
        gap: 1rem;
        text-align: center;
    }

    .qr-stats {
        gap: 1rem;
    }

    .qr-notification {
        right: 10px;
        left: 10px;
        width: auto;
    }
}

