// Regenerate QR code for existing session
app.post('/api/faculty/regenerate-qr/:sessionId', authenticateToken, requireFaculty, (req, res) => {
  const { sessionId } = req.params;

  // Verify session exists and belongs to faculty
  db.get('SELECT * FROM sessions WHERE session_id = ? AND faculty_id = ?', [sessionId, req.user.userId], (err, session) => {
    if (err) {
      return res.status(500).json({ error: 'Database error' });
    }

    if (!session) {
      return res.status(404).json({ error: 'Session not found or unauthorized' });
    }

    // Check if session is not expired
    const now = new Date();
    const expiresAt = new Date(session.expires_at);
    if (now > expiresAt) {
      return res.status(400).json({ error: 'Cannot regenerate QR for expired session' });
    }

    // Generate new expiry time (2 minutes from now)
    const newExpiresAt = new Date(Date.now() + 2 * 60 * 1000);

    // Update session expiry
    db.run('UPDATE sessions SET expires_at = ? WHERE session_id = ?', [newExpiresAt, sessionId], (updateErr) => {
      if (updateErr) {
        return res.status(500).json({ error: 'Failed to update session' });
      }

      // Smart environment detection for QR code URLs
      const isReplit = !!process.env.REPLIT_DEV_DOMAIN;
      let domain, protocol;

      if (isReplit) {
        domain = process.env.REPLIT_DEV_DOMAIN;
        protocol = 'https';
      } else {
        domain = `localhost:5500`;
        protocol = 'http';
      }

      const checkInURL = `${protocol}://${domain}/checkin.html?sessionId=${sessionId}&subject=${encodeURIComponent(session.subject)}&room=${encodeURIComponent(session.room)}`;

      // Generate new QR code
      QRCode.toDataURL(checkInURL, {
        errorCorrectionLevel: 'H',
        margin: 2,
        width: 400,
        color: {
          dark: '#000000',
          light: '#ffffff'
        }
      })
        .then(qrCodeURL => {
          // Update in-memory storage
          const existingSession = activeQRCodes.get(sessionId);
          if (existingSession) {
            existingSession.expiresAt = newExpiresAt;
            existingSession.checkInURL = checkInURL;
          }

          res.json({
            success: true,
            sessionId,
            qrCode: qrCodeURL,
            expiresAt: newExpiresAt.toISOString(),
            checkInURL,
            message: 'QR code regenerated successfully'
          });

          // Emit regeneration event to faculty dashboard
          io.emit('qr_regenerated', {
            sessionId,
            expiresAt: newExpiresAt.toISOString(),
            qrCode: qrCodeURL
          });

          console.log(`ðŸ”„ QR Code regenerated: ${session.subject} - ${sessionId.slice(0, 8)}... (new expiry: 2 minutes)`);
        })
        .catch(error => {
          console.error('QR regeneration error:', error);
          res.status(500).json({ error: 'Error regenerating QR code' });
        });
    });
  });
});

// Real QR code scanning endpoint - FANG Level
app.post('/api/student/mark-attendance', (req, res) => {
  const { sessionId, studentEmail, timestamp, location } = req.body;

  if (!sessionId || !studentEmail) {
    return res.status(400).json({ error: 'Session ID and student email are required' });
  }

  // Check if session exists and is not expired
  const session = activeQRCodes.get(sessionId);
  if (!session) {
    return res.status(400).json({ error: 'Invalid or expired QR code' });
  }

  if (new Date() > session.expiresAt) {
    activeQRCodes.delete(sessionId);
    return res.status(400).json({ error: 'QR code has expired (2 minutes limit)' });
  }

  // Validate geolocation if required
  if (session.geoRequired) {
    if (!location || !location.latitude || !location.longitude) {
      return res.status(400).json({ 
        error: 'Location access is required for this session. Please enable location services and try again.' 
      });
    }

    const distance = calculateDistance(
      session.latitude,
      session.longitude,
      location.latitude,
      location.longitude
    );

    if (distance > session.radius) {
      return res.status(403).json({ 
        error: `You are ${Math.round(distance)}m away from the class location. You must be within ${session.radius}m to mark attendance.`,
        distance: Math.round(distance),
        requiredRadius: session.radius,
        userLocation: location,
        sessionLocation: { latitude: session.latitude, longitude: session.longitude }
      });
    }

    console.log(`âœ… Geolocation validated: Student is ${Math.round(distance)}m away (allowed: ${session.radius}m)`);
  }

  // Get student details by email
  db.get('SELECT * FROM students WHERE email = ?', [studentEmail], (err, student) => {
    if (err) {
      return res.status(500).json({ error: 'Database error' });
    }

    if (!student) {
      return res.status(404).json({ error: 'Student not found' });
    }

    // Determine attendance status based on time
    const scanTime = new Date(timestamp);
    const sessionStart = new Date(session.expiresAt.getTime() - 2 * 60 * 1000); // 2 minutes before expiry
    const timeDiff = (scanTime - sessionStart) / 1000; // seconds
    const status = timeDiff <= 60 ? 'present' : 'late'; // First minute = present, after = late

    // Record attendance
    db.run(
      'INSERT OR REPLACE INTO attendance (session_id, student_id, status, timestamp) VALUES (?, ?, ?, ?)',
      [sessionId, student.student_id, status, timestamp],
      function(err) {
        if (err) {
          return res.status(500).json({ error: 'Failed to record attendance' });
        }

        res.json({
          success: true,
          message: 'Attendance marked successfully',
          studentName: student.name,
          subject: session.subject,
          status: status,
          timestamp: timestamp,
          sessionId: sessionId
        });

        // ðŸš€ ENHANCED Real-time update to faculty dashboard
        const realTimeData = {
          sessionId: sessionId,
          studentId: student.student_id,
          studentName: student.name,
          studentEmail: studentEmail,
          subject: session.subject,
          status: status,
          timestamp: timestamp,
          location: location,
          // Additional data for enhanced UI updates
          scanTime: new Date().toLocaleTimeString(),
          timeDifference: timeDiff
        };

        // Emit to all connected faculty dashboards
        io.emit('attendance_marked', realTimeData);

        // ðŸ”’ FIXED: Emit to specific faculty room using consistent facultyId format
        io.to(`faculty_${session.facultyId}`).emit('attendance_update', realTimeData);

        console.log(`ðŸ“¡ Real-time update sent: ${student.name} marked ${status}`);

        console.log(`âœ… Attendance marked: ${student.name} (${student.email}) - ${status} in ${session.subject}`);
      }
    );
  });
});

